"beforeLoadProductView = function() {

    ijf.snippets.onOpenApplication = function() {
        window.open(location.href.replace(/formId=.*?&/,"formId=" + ijf.main.outerForm.formSet.forms[0].name + "&"));
    }

    ijf.snippets.isUserJiraAdmin = function() {
        return ijf.main.currentUser.groupList.reduce(function(inB, g) {
            if (["administrators", "jira-adminisrators", "site-admins"].indexOf(g) > -1)
                inB = true;
            return inB;
        }, false);
    }

    ijf.snippets.onDeleteVersion = function() {
        var id = "ijfContent_fld_13,8";

        //Show in form

        var vid = ijf.main.controlSet[id].control.items.items[0].getValue();

        if ((!vid) || (vid == 0) || (isNaN(vid)))
            return;

        var delIt = function() {

            var results = ijfUtils.jiraApiSync("DELETE", '/ift/rest/version/' + vid, null);

            if (results == "OK") {

                var delKeys = ["ijfContent_fld_13,8", "ijfContent_fld_14,8", "ijfContent_fld_15,8", "ijfContent_fld_16,8", "ijfContent_fld_17,8"];
                delKeys.forEach(function(f) {
                    ijf.session[f] = null;
                });
                ijf.main.resetForm();

            } else {
                ijfUtils.modalDialogMessage("Error", "Failed to delete: " + results);
            }

        }
        ijfUtils.modalDialog("Warning", "Are you sure you want to delete this product version?", delIt);

    }

    ijf.snippets.onIftVersionsDblClick = function(f, n, rec) {

        var pid = "ijfContent_fld_20,8";

        var id = "ijfContent_fld_13,8";
        var version = "ijfContent_fld_14,8";
        var author = "ijfContent_fld_15,8";
        var description = "ijfContent_fld_16,8";
        var config = "ijfContent_fld_17,8";

        var prodId = ijf.main.controlSet[pid].control.items.items[0].getValue();

        //get full version...
        var thisVersion = ijfUtils.jiraApiSync("GET", '/ift/rest/product/' + prodId + '/version/' + rec.get("id"), null);

        //Show in form

        ijf.main.controlSet[id].control.items.items[0].setValue(rec.get("id"));

        ijf.main.controlSet[version].control.items.items[0].setValue(rec.get("Version"));

        ijf.main.controlSet[author].control.items.items[0].setValue(rec.get("Author"));

        ijf.main.controlSet[description].control.items.items[0].setValue(rec.get("Description"));

        ijf.main.controlSet[config].control.items.items[0].setValue(thisVersion.result);

    }

    ijf.snippets.onAddEditVersion = function() {
        var pid = "ijfContent_fld_20,8";
        var id = "ijfContent_fld_13,8";
        var version = "ijfContent_fld_14,8";
        var author = "ijfContent_fld_15,8";
        var description = "ijfContent_fld_16,8";
        var config = "ijfContent_fld_17,8";

        //save the product to the db...simply

        var pObj = {
            "pid": ijf.main.controlSet[pid].control.items.items[0].getValue(),
            "id": ijf.main.controlSet[id].control.items.items[0].getValue(),
            "version": ijf.main.controlSet[version].control.items.items[0].getValue(),
            "author": ijf.main.controlSet[author].control.items.items[0].getValue(),
            "description": ijf.main.controlSet[description].control.items.items[0].getValue(),
            "config": ijf.main.controlSet[config].control.items.items[0].getValue()
        };

        var jOut = {
            "action": "saveProductVersion",
            "jsonConfig": JSON.stringify(pObj)
        };

        var results = ijfUtils.jiraApiSync("POST", '/ift/rest/addeditversion', jOut, "application/x-www-form-urlencoded; charset=UTF-8");

        if (results.status == "OK") {
            var delKeys = ["ijfContent_fld_13,8", "ijfContent_fld_14,8", "ijfContent_fld_15,8", "ijfContent_fld_16,8", "ijfContent_fld_17,8"];
            delKeys.forEach(function(f) {
                ijf.session[f] = null;
            });
            ijf.main.resetForm();
        } else {
            ijfUtils.modalDialogMessage("Error", "Failed to save: " + results.message);
        }

    }

    ijf.snippets.onAddEditVersiononDeleteProduct = function() {
        var id = "ijfContent_fld_20,8";

        //Show in form

        var pid = ijf.main.controlSet[id].control.items.items[0].getValue();

        if ((!pid) || (pid == 0) || (isNaN(pid)))
            return;

        var delIt = function() {

            var results = ijfUtils.jiraApiSync("DELETE", '/ift/rest/product/' + pid, null);

            if (results == "OK") {

                var delKeys = ["ijfContent_fld_20,8", "ijfContent_fld_21,8", "ijfContent_fld_22,8", "ijfContent_fld_23,8"];
                delKeys.forEach(function(f) {
                    ijf.session[f] = null;
                });
                ijf.main.resetForm();

            } else {
                ijfUtils.modalDialogMessage("Error", "Failed to delete: " + results);
            }

        }
        ijfUtils.modalDialog("Warning", "Are you sure you want to delete this product?", delIt);

    }

    ijf.snippets.onLoadProductView = function() {

            ijf.snippets.beforeLoadSetSystem();
			var ijfRoot = getIjfRoot();
			var baseSys = ijf.main.baseSys;

   			var row1 = "ijfContent_fld_2,3";
			var row2 = "ijfContent_fld_4,3";
			var row3 = "ijfContent_fld_6,3";
			var row4 = "ijfContent_fld_8,3";

			var currentForm = ijf.main.outerForm;
			var currentFG = currentForm.formSet;
			var iftProductName = currentFG.iftFormGroup;

			var products =  ijfUtils.jiraApiSync("GET",'/ift/rest/products',null);

            var iftProduct = products.resultSet.reduce(function(inV,p){							 
			   if(p.productId==iftProductName) inV=p; return inV;
 		    },null);





        var productGridKey = "ijfContent_fld_20,3";

        //get the list of products and render in the session grid
        var pGrid = ijf.main.controlSet[productGridKey].control;
        var pStore = pGrid.getStore();

        var pRow = null;


        products.resultSet.forEach(function(p) {
            if (p.hasOwnProperty("id")) {
                pRow = Ext.create(pStore.model);
                pRow.set("id", p.id);
                pRow.set("Name", p.name);
                pRow.set("Description", p.description);
                pRow.set("Product ID", p.productId);
                pRow = pStore.insert(0, pRow);
            }
        });
        pStore.commitChanges();

    }
	ijf.snippets.onOpenFormDesigner = function()
	{
			//do a standard popform....
		var targetDiv = document.getElementById("iftProductForms_id");
		var productForms = ["IFT My Worklogs","IFT Worklog Report"]


		var outHtml = "<br>"

		outHtml = "The IFT Worklogs product is built and configured using Ideal Forms for JIRA.  This product includes a portion of Ideal Forms that allows you to edit and modify your version of Worklogs.  The links below will open the forms designer for your two forms.  When you save, the system will continually backup your current version and it will keep the last 50 versions you save which you can revert to at any time.  The designer is complex so please reach out with questions.  Adding columns to a display is pretty easy, altering behavoir will requires some javascript coding.<br>";

		outHtml += ijf.main.outerForm.formSet.forms.reduce(function(inV, f){
			if(productForms.indexOf(f.name)>-1)
				inV += "<br><a target='_blank' href='"+getIjfRoot()+"/run?debug=true&craft=true&itemId=&formId="+f.name+"&jwt="+getJwtToken()+"'>Click here to edit "+f.name+"</a>";			
			return inV;
			},"");

		targetDiv.innerHTML=outHtml;
		targetDiv.style.marginToptop="5px";
		targetDiv.style.marginLeft="10px";		
		targetDiv.style.display='inline-block';


	}
    ijf.snippets.onAddEditProduct = function() {
        var id = "ijfContent_fld_20,8";
        var productName = "ijfContent_fld_21,8";
        var productDesc = "ijfContent_fld_22,8";
        var productId = "ijfContent_fld_23,8";

        //save the product to the db...simply

        var pObj = {
            "id": ijf.main.controlSet[id].control.items.items[0].getValue(),
            "productName": ijf.main.controlSet[productName].control.items.items[0].getValue(),
            "productDesc": ijf.main.controlSet[productDesc].control.items.items[0].getValue(),
            "productId": ijf.main.controlSet[productId].control.items.items[0].getValue()
        };

        var jOut = {
            "action": "saveProduct",
            "jsonConfig": JSON.stringify(pObj)
        };

        var results = ijfUtils.jiraApiSync("POST", '/ift/rest/addeditproduct', jOut, "application/x-www-form-urlencoded; charset=UTF-8");

        if (results.status == "OK") {
            ijf.main.resetForm();
        } else {
            ijfUtils.modalDialogMessage("Error", "Failed to save: " + results.message);
        }

    }

    ijf.snippets.onIftProductsDblClick = function(f, n, rec) {

        var id = "ijfContent_fld_20,8";
        var productName = "ijfContent_fld_21,8";
        var productDesc = "ijfContent_fld_22,8";
        var productId = "ijfContent_fld_23,8";

        var versionsGrid = "ijfContent_fld_13,3";

        //Show in form

        ijf.main.controlSet[id].control.items.items[0].setValue(rec.get("id"));

        ijf.main.controlSet[productName].control.items.items[0].setValue(rec.get("Name"));

        ijf.main.controlSet[productDesc].control.items.items[0].setValue(rec.get("Description"));

        ijf.main.controlSet[productId].control.items.items[0].setValue(rec.get("Product ID"));

        //now versions grid...
        //cleare the fields
        var delKeys = ["ijfContent_fld_13,8", "ijfContent_fld_14,8", "ijfContent_fld_15,8", "ijfContent_fld_16,8", "ijfContent_fld_17,8"];
        delKeys.forEach(function(f) {
            ijf.main.controlSet[f].control.items.items[0].setValue("");
            ijf.session[f] = null;
        });

        var vGrid = ijf.main.controlSet[versionsGrid].control;
        var vStore = vGrid.getStore();

        vStore.loadData([]);

        var vRow = null;

        var versions = ijfUtils.jiraApiSync("GET", '/ift/rest/product/' + rec.get("id") + '/versions', null);

        versions.resultSet.forEach(function(p) {
            if (p.hasOwnProperty("id")) {
                vRow = Ext.create(vStore.model);
                vRow.set("id", p.id);
                vRow.set("Version", p.version);
                vRow.set("Author", p.author);
                vRow.set("Date", p.date);
                vRow.set("Description", p.description);
                vRow.set("config", p.config);
                vRow = vStore.insert(0, vRow);
            }
        });
        vStore.commitChanges();

    }

}
