"onMovePreviousPeriod=function()
{

   switch(ijf.session.periodMode)
   {
       case "week":

           ijf.session.fromDate= moment(ijf.session.fromDate).subtract('week',1);
           ijf.session.toDate= moment(ijf.session.toDate).subtract('week',1);

         break;
       case "month":

           ijf.session.fromDate.startOf('month').subtract('month',1);  
           ijf.session.toDate =  moment(ijf.session.fromDate).endOf('month');

         break;
       case "4 weeks":

           ijf.session.fromDate= moment(ijf.session.fromDate).subtract('day',7);
           ijf.session.toDate= moment(ijf.session.toDate).subtract('day',7);


         break;
       default:
          return;
          break;

   }
ijf.snippets.closePops();
ijfUtils.showProgress();
var resetForm = function()
{

ijf.main.resetForm();
ijfUtils.hideProgress();
}
     window.setTimeout(resetForm,50);

}"

