"onChangeTimeframe = function(f, n, o) {

    var fromDate = null;
    var toDate = null;

    var tf = n;

    if (!n)
        return;

    ijf.session.filterMode = "timeframe";

    var timeFrameName = "Current Week";

    switch (n) {
    case "0":
        //today

        var fromDate = moment(new Date());
        var toDate = moment(new Date());
        timeFrameName = "0";

        break;
    case "1":
        //current week

        var fromDate = moment().startOf('isoWeek');
        var toDate = moment().endOf('isoWeek');
        //moment(new Date());
        timeFrameName = "1";

        break;
    case "2":
        //last week

        var fromDate = moment(moment().subtract('week', 1)).startOf('isoWeek');
        var toDate = moment(moment().subtract('week', 1)).endOf('isoWeek');
        timeFrameName = "2";

        break;
    case "3":
        //current Month

        var fromDate = moment().startOf('month');
        var toDate = moment(new Date());
        timeFrameName = "3";

        break;
    case "4":
        //last Month

        var fromDate = moment(moment().subtract('month', 1)).startOf('month');
        var toDate = moment(moment().subtract('month', 1)).endOf('month');
        timeFrameName = "4";

        break;
    case "5":
        //current Year

        var fromDate = moment().startOf('year');
        var toDate = moment(new Date());
        timeFrameName = "5";

        break;
    case "6":
        //last Year

        var fromDate = moment(moment().subtract('year', 1)).startOf('year');
        var toDate = moment(moment().subtract('year', 1)).endOf('year');
        timeFrameName = "6";

        break;
    case "7":
        //last 7 days

        var fromDate = moment(new Date()).subtract('days', 7);
        var toDate = moment(new Date());
        timeFrameName = "7";

        break;
    case "8":
        //all

        var fromDate = moment(moment().subtract('year', 10)).startOf('year');
        var toDate = moment(new Date());
        timeFrameName = "8";

        break;
    default:
        return;
        break;

    }

    //clear dates
    var fromDateCtl = ijf.main.controlSet["ijfContent_fld_4,3"].control.items.items[0];
    var toDateCtl = ijf.main.controlSet["ijfContent_fld_4,4"].control.items.items[0];
    fromDateCtl.suspendEvents();
    fromDateCtl.setValue(new Date(fromDate));
    fromDateCtl.resumeEvents();
    toDateCtl.suspendEvents();
    toDateCtl.setValue(new Date(toDate));
    toDateCtl.resumeEvents();

    ijf.session["ijfContent_fld_4,3"] = fromDate.format('YYYY-M-D');
    ijf.session["ijfContent_fld_4,4"] = toDate.format('YYYY-M-D');

    ijf.session.wlFromDate = fromDate.format('YYYY-M-D');
    ijf.session.wlToDate = toDate.format('YYYY-M-D');

    //run filter
    var curJql = ijf.main.controlSet["ijfContent_fld_6,2"].field.dataSource
    curJql = curJql.replace(/and worklogDate.*?=.*?\d\d\d\d-\d.*?-\d*/ig, "");

    //alter query to add clause unless it's the ALL call

    var qParts = curJql.split(/order by/i);
    if (qParts.length > 1)
        curJql = qParts[0].trim() + " and worklogDate >= " + fromDate.format('YYYY-M-D') + " and worklogDate <= " + toDate.format('YYYY-M-D') + " order by " + qParts[1].trim();
    else
        curJql = curJql.trim() + " and worklogDate >= " + fromDate.format('YYYY-M-D') + " and worklogDate <= " + toDate.format('YYYY-M-D');


    ijf.main.controlSet["ijfContent_fld_6,2"].field.dataSource = curJql;

    //history.replaceState({}, 'WLR', '/plugins/servlet/iforms?decorator=general&formId=Worklog%20Report&jql=' + curJql + '&timeframe=' + timeFrameName);
    //history.replaceState({}, 'WLR', '/plugins/servlet/iforms?decorator=general&formId=Worklog%20Report&timeframe=' + timeFrameName);
    //with radio, cannot just reset the form because the group needs to complete...set timeout for +200ms

    var resetForm = function() {
        ijf.main.renderForm("ijfContent", "IFT Worklog Report", false, ijf.currentItem);
        ijfUtils.hideProgress();
    };
    ijfUtils.showProgress();
    window.setTimeout(resetForm, 10);
}
"

