"setWorklogDateFilter=function(f,n,o)
{



  ijf.session.filterMode = "custom";



  var timeFrame = ijf.main.controlSet["ijfContent_fld_4,2"].control.items.items[0];
  timeFrame.suspendEvents(true);
  timeFrame.setValue("");
