var toDate = ijf.main.controlSet["ijfContent_fld_4,4"].control.items.items[0].getValue();

if(toDate==null)
{
    toDate = moment(new Date()).format('YYYY-M-D');
}
else
{
   toDate =moment(toDate).format('YYYY-M-D');
}

if(fromDate==null)
{
    fromDate = '2000-1-1';
}
else
{
   fromDate =moment(fromDate ).format('YYYY-M-D');
}

//set dates
var fromDateCtl = ijf.main.controlSet["ijfContent_fld_4,3"].control.items.items[0];
var toDateCtl = ijf.main.controlSet["ijfContent_fld_4,4"].control.items.items[0];
fromDateCtl.disable(true);
fromDateCtl.setValue(new Date(fromDate));
fromDateCtl.enable();
toDateCtl .disable(true);
toDateCtl .setValue(new Date(toDate));
toDateCtl .enable(true);
ijf.session.wlFromDate = fromDate;
ijf.session.wlToDate = toDate;

var curJql =  ijf.snippets.JqlFramework.getJQL();

curJql = curJql.replace(/and worklogDate.*?=.*?\d\d\d\d-\d.*?-\d*/ig,"");

curJql = curJql.trim();;

                //alter query to add clause
                var qParts= curJql.split(/order by/i);
                if(qParts.length>1)
                     curJql = qParts[0].trim() + " and worklogDate >= "+ fromDate+" and worklogDate <= "+toDate + " order by " +qParts[1].trim();
                else
                     curJql = curJql +  " and worklogDate >= "+ fromDate+" and worklogDate <= "+toDate;

ijf.main.controlSet["ijfContent_fld_6,2"].field.dataSource=curJql;
ijf.snippets.JqlFramework.setJQL(curJql);


   //with radio, cannot just reset the form because the group needs to complete...set timeout for +200ms
   var resetForm = function(){   ijf.main.renderForm("ijfContent", "IFT Worklog Report", false, ijf.currentItem);};
   window.setTimeout(resetForm,100);


   return true;

}"

