    checkJQL: function(comboObjRef) {

        //try to parse advanced string
        var currentString = Ext.getCmp('ijfContent_ctr_AdvancedSearchText').getValue();
        //if parseable then return true;
        if (currentString != "") {
            var parsed = ijf.snippets.JqlFramework.parseJQL(currentString);
            //create an array of objects {of comboRefs and new values} from the user //might need lookupV if ref is dif in store.
            if (parsed.queries[0].structure.where.operator != "or") {
                if (parsed.queries[0].structure.where.clauses == undefined) {
                    var base = [parsed.queries[0].structure.where];
                } else {
                    var base = parsed.queries[0].structure.where.clauses;
                }

                //////////
                var listOfAllowed = ["project", "issuetype", "status", "assignee", "text"];
                var listOfNotAllowed = [];
                var Len = base.length;
                base.forEach(function(J) {
                    var isbasic = false;
                    listOfAllowed.some(function(A, P) {
                        if (J.field.name == A) {
                            isbasic = true;
                            listOfAllowed.splice(P, 1);
                            return true;
                        }
                    })
                    if (!isbasic) {
                        listOfNotAllowed.push(J);
                    }
                })

                if (listOfNotAllowed.length == 0) {
                    base.forEach(function(Q) {
                        var i = comboObjRef.findIndex(function(cValue, indexA, arr) {
                            return cValue.id === Q.field.name
                        });
                        //var ref = comboObjRef[i];
                        //if q == text then don't do this just set text.
                        if (Q.field.name == "assignee") {
                            ijf.snippets.JqlFramework.updateCombo(comboObjRef[i].comboR, Q.operand.values[0].value, comboObjRef[i].lookupV);
                        } else if (Q.field.name == "text") {
                            comboObjRef[i].comboR.setValue(Q.operand.value);
                        } else {
                            ijf.snippets.JqlFramework.updateCombo(comboObjRef[i].comboR, Q.operand.value, comboObjRef[i].lookupV);
                        }
                        //ijf.snippets.JqlFramework.updateCombo(combo,value,lookupV)
                    })

                    return true;
                } else {
                    return false;
                }
                //////////////////////
            } else {
                return false;
            }
        } else {
            //should really set all fields to ""; **Maybe do this in update!
            return true;
        }
    },
    parseJQL: function(JQLString) {
        var jData = {
            "queries": [JQLString]
        };
        var inData = JSON.stringify(jData);
        var retVal = "tbd";
        jQuery.ajax({
            headers: {
                Accept: "application/json"
            },
            async: false,
            type: 'POST',
            contentType: "application/json; charset=utf-8",
            url: g_root + "/rest/api/2/jql/parse",
            data: inData,
            timeout: 60000,
            success: function(data, e, f) {
                ijfUtils.footLog("Successful data response code: " + f.status);
                if ((f.status == 200) || (f.status == 201) || (f.status == 204)) {
                    if ((this.type == "GET") || (this.type == "POST"))
                        retVal = data;
                    else
                        retVal = "OK";
                } else {
                    retVal = "Unsuccessful Api Call: " + f.status;
                }
            },
            error: function(e) {
                if ((e.status == 201) || (e.status == 200)) //  && (e.statusText=="Created"))
                {
                    if (this.type == "GET")
                        retVal = e.responseText;
                    else
                        retVal = "OK";
                } else if (e.statusText == "OK") {
                    ijfUtils.footLog("Successful data post");
                    retVal = e.responseText;
                } else {
                    ijfUtils.footLog("Failed data post: " + " " + e.statusText);
                    if (e.status == 403) {
                        retVal = "You do not have permission to edit.";
                    } else {
                        retVal = "Failed data put: " + e.statusText + " " + e.responseText;
                    }
                }
            }
        });
        return retVal;
    },
    updateCombo: function(combo, value, lookupV) {
        var int = 0;
        //simple.items.items[0], var value = 'AMT', lookupV replace key with what lookup is needed.
        var cStore = combo.store;
        var valueField = combo.valueField;
        var displayField = combo.displayField;

        var recordNumber = cStore.getData().items.findIndex(function(cValue, indexA, arr) {
            return cValue.data[lookupV[int]] === value
        });
        if (recordNumber == -1) {
            int++;
            var recordNumber = cStore.getData().items.findIndex(function(cValue, indexA, arr) {
                return cValue.data[lookupV[int]] === value
            });
            if (recordNumber == -1) {
                int++;
                var recordNumber = cStore.getData().items.findIndex(function(cValue, indexA, arr) {
                    return cValue.data[lookupV[int]] === value
                });
            }
        }

        var displayValue = cStore.getAt(recordNumber).data[displayField];

        //combo.select(value);
        combo.setValue(value);
        combo.setRawValue(displayValue);
        var record = cStore.getAt(recordNumber);
        combo.fireEvent('select', combo, record);
        //
        //not working
        //combo.selectedIndex = recordNumber;
    },
    updateAdvancedString: function() {
        //
        var opt = ['projectJQL', 'typeJQL', 'statusJQL', 'assigneeJQL', 'basicTextJQL'];
        var returnJQLString = '';
        var arrayJQL = [];
        opt.forEach(function(O, P) {
            var ref = ijf.snippets.JqlFramework[O];
            if (Object.keys(ref).length != 0) {
                //make sure the option is not empty
                if (P == 0) {
                    arrayJQL.push('project = ' + ref.key);
                } else if (P == 1) {
                    arrayJQL.push('issuetype = "' + ref.name + '"');
                } else if (P == 2) {
                    arrayJQL.push('status = "' + ref.name + '"');
                } else if (P == 3) {
                    arrayJQL.push('assignee in ("' + ref.accountId + '")');
                } else if (P == 4) {
                    if (ref.value != '') {
                        arrayJQL.push('text ~ "' + ref.value + '"');
                    }
                }
            }
        })
        //after combined all of the arrayJQL with AND inbetween;
        arrayJQL.forEach(function(J, P) {
            if (P == 0) {
                returnJQLString = J;
            } else {
                returnJQLString += " AND " + J;
            }
        })
        //gets called when user clicks the toggle button.
        Ext.getCmp('ijfContent_ctr_AdvancedSearchText').setValue(returnJQLString);
    },
