        var JQLTMain1 = Ext.get("JQLTMainDiv1");
        var JQLTMain2 = Ext.get("JQLTMainDiv2");
        if (JQLTMain2.dom.style.display == 'none') {
            //we are in advanced
            //just return the string
            returnJQLString = Ext.getCmp('ijfContent_ctr_AdvancedSearchText').getValue();
        } else if (JQLTMain2.dom.style.display == '') {
            //we are in basic;
            //based on this build the jql
            var opt = ['projectJQL', 'typeJQL', 'statusJQL', 'assigneeJQL', 'basicTextJQL'];
            var arrayJQL = [];
            opt.forEach(function(O, P) {
                var ref = ijf.snippets.JqlFramework[O];
                if (Object.keys(ref).length != 0) {
                    //make sure the option is not empty
                    if (P == 0) {
                        arrayJQL.push('project = ' + ref.key);
                    } else if (P == 1) {
                        arrayJQL.push('issuetype = "' + ref.name + '"');
                    } else if (P == 2) {
                        arrayJQL.push('status = "' + ref.name + '"');
                    } else if (P == 3) {
                        arrayJQL.push('assignee in ("' + ref.accountId + '")');
                    } else if (P == 4) {
                        if (ref.value != '') {
                            arrayJQL.push('text ~ "' + ref.value + '"');
                        }
                    }
                }
            })
            //after combined all of the arrayJQL with AND inbetween;
            arrayJQL.forEach(function(J, P) {
                if (P == 0) {
                    returnJQLString = J;
                } else {
                    returnJQLString += " AND " + J;
                }
            })

        }
        return returnJQLString;
    }
}
