    handleSuccessAdd: function(cWl, cPan, d, r, e, inContainerId, grid, gridRow, gridColumn, wlHoursOG, wlHoursNW, wlDateOG, wlDateNW, wlCommentOG, wlCommentNW, wlKeyOG, wlKeyNW, newSummary) {
        cPan.update("Saved");
        //this means I've added a WL entry.  I have it.  it may or may not be in view...
        //if in view, add or find Data row.  Update as needed...if self, just force them to close and open grid again

        //OGs don't exist
        isDateChangeInView=false;
        var gColKey = moment(wlDateNW).format("dd MM/DD");
        var inViewColumn = grid.getColumns().reduce(function(inV, c) {
            if (c.text == gColKey) {
                inV = c;
                isDateChangeInView = true;
            }
            return inV;
        }, null);

        if (isDateChangeInView) {
            //get data row
            //is the new row ...ie need to add in date change...
            var targetKey = wlKeyOG;
            if (wlKeyNW)
                targetKey = wlKeyNW;

            var dataRow = grid.getStore().getData().items.reduce(function(inV, r) {
                if (r.data.iid == targetKey)
                    inV = r;
                return inV
            }, null);
            if (!dataRow) {
                //need to add the new issue to the current grid view...   
                var wlRow = Ext.create(grid.store.model);
                wlRow.set("Key", "<a title='Click to edit Issue' href=" + ijf.main.baseSys + "/browse/" + targetKey + "' target='_blank'>" + targetKey + "</a>");
                wlRow.set("Summary", cWl.summary);
                wlRow.set("iid", targetKey);

                var rCount = grid.store.getData().items.length;
                wlRow = grid.store.insert(rCount, wlRow);
                dataRow = wlRow[0];
                //new dr, add it's worklog array
                ijf.session.myWorklogData[targetKey + "_" + inViewColumn.text] = {
                    worklogs: []
                }
            } else {
                if (!ijf.session.myWorklogData.hasOwnProperty(targetKey + "_" + inViewColumn.text))
                    ijf.session.myWorklogData[wlKeyNW + "_" + inViewColumn.text] = {
                        worklogs: []
                    }
            }
            //data row was on view and needs the animal added...
            var cellModel = ijf.session.myWorklogData[wlKeyNW + "_" + inViewColumn.text];

            //need to work this to configure a wl entry for the added wl adn put in the proper place...
            cellModel.worklogs.push(cWl);

            //update the row value and hours
            var tVal = dataRow.get(inViewColumn.dataIndex) / 1;
            tVal = tVal + cWl.wlHours / 1;
            dataRow.set(inViewColumn.dataIndex, tVal);

            tVal = dataRow.get('Hours') / 1;
            tVal = tVal + cWl.wlHours / 1;
            dataRow.set('Hours', tVal);
            dataRow.commit();
            resMessage = "Worklog added to issue, please reopen this dialog to refresh";

        } else {
            //simply update so say not date in view
            resMessage = "Worklog added to issue outside your current view.";
        }

        cPan.update(resMessage);
        cPan.items.items[5].setHidden("true");

    },
