                    wlRow.set("Key", "<a title='Click to edit Issue' href=" + ijf.main.baseSys + "/browse/" + wlKeyNW + "' target='_blank'>" + wlKeyNW + "</a>");
                    wlRow.set("Summary", copiedWl.summary);
                    wlRow.set("iid", wlKeyNW);

                    var rCount = grid.store.getData().items.length;
                    wlRow = grid.store.insert(rCount, wlRow);
                    dataRow = wlRow[0];
                    //new dr, add it's worklog array
                    ijf.session.myWorklogData[wlKeyNW + "_" + inViewColumn.text] = {
                        worklogs: []
                    }
                } else {
                    if (!ijf.session.myWorklogData.hasOwnProperty(wlKeyNW + "_" + inViewColumn.text))
                        ijf.session.myWorklogData[wlKeyNW + "_" + inViewColumn.text] = {
                            worklogs: []
                        }
                }
                //data row was on view and needs the animal added...
                var cellModel = ijf.session.myWorklogData[wlKeyNW + "_" + inViewColumn.text];

                //have dataRow and have inViewColumn 
                //data row could not have had a wl entry because it would have been in view...so just add the modified WL entry to the wlstore, recalc and done 
                cellModel.worklogs.push(copiedWl);

                //update the row value and hours
                var tVal = dataRow.get(inViewColumn.dataIndex) / 1;
                tVal = tVal + copiedWl.wlHours / 1;
                dataRow.set(inViewColumn.dataIndex, tVal);

                tVal = dataRow.get('Hours') / 1;
                tVal = tVal + copiedWl.wlHours / 1;
                dataRow.set('Hours', tVal);
                dataRow.commit();
                resMessage = "Worklog moved to new issue, please reopen any dialog for the new issue day.";

            } else {
                resMessage = "Worklog moved to date not in current view.";
            }

        } else {
            //look for date change of log for current key

            if (wlDateNW) {

                isDateChange = true;
                //OK, the date changed.  it's either in the range or not..do a reduce on gGols

                //reduce current by full.. the current 
                var curHours = gridRow.get(gridColumn.dataIndex);
                var newHrs = curHours - cWl.wlHours / 1;
                gridRow.set(gridColumn.dataIndex, newHrs);

                var gCols = grid.getColumns();
                var gColKey = moment(wlDateNW).format("dd MM/DD");
                var inViewColumn = gCols.reduce(function(inV, c) {
                    if (c.text == gColKey) {
                        inV = c;
                        isDateChangeInView = true;
                    }
                    return inV;
                }, null);
                if (isDateChangeInView) {
                    //new cell
                    curHours = gridRow.get(inViewColumn.dataIndex);
                    newHrs = curHours + cWl.wlHours / 1;
                    gridRow.set(inViewColumn.dataIndex, newHrs);

                    //now move the worklog entry from one place in array to another....
                    var sourceArray = ijf.session.myWorklogData[wlKeyOG + "_" + gridColumn.text];

                    var wlIndex = sourceArray.worklogs.reduce(function(inV, w, i) {
                        if (w.wlRaw.id == cWl.wlRaw.id)
                            inV = i;
                        return inV;
                    }, null);
                    var copiedWl = sourceArray.worklogs.splice(wlIndex, 1);
                    copiedWl[0].wlStarted = moment(wlDateNW).format("YYYY-MM-DDTHH:mm:ss.SSS") + "-0500";

                    //ijf.session.myWorklogData[wlKeyOG + "_" + gridColumn.text]

                    var targetArray = ijf.session.myWorklogData[wlKeyOG + "_" + gColKey];
                    if (!targetArray) {
                        targetArray = {
                            "worklogs": []
                        };
                        ijf.session.myWorklogData[wlKeyOG + "_" + gColKey] = targetArray;
                    }
                    targetArray.worklogs.push(copiedWl[0]);

                } else {
                    var sourceArray = ijf.session.myWorklogData[wlKeyOG + "_" + gridColumn.text];
                    delete cWl;
                    var sourceArray = ijf.session.myWorklogData[wlKeyOG + "_" + gridColumn.text];
                }
                gridRow.commit();
            }
        }

        var delayIt = function() {
            if (isIssueChange) {
                if (!isDateChangeInView) {
                    cPan.update("Entry has been moved to an Issue and date out of your current view");
                } else {
                    cPan.update("Entry moved to new issue in your view.  Please reopen any open dialogs for that date.");
                }
                cPan.items.items[5].setHidden("true");
            } else if (isDateChange) {

                if (!isDateChangeInView) {
                    cPan.update("Entry has been moved to a date out of your current view");
                } else {
                    cPan.update("Entry moved to new date.  Please reopen any open dialogs for that date.");
                }
                cPan.items.items[5].setHidden("true");

            } else {
                cPan.unmask();
                cPan.items.items.forEach(function(c) {
                    c.fieldDirty = false;
                });
                cPan.update("");
                cPan.items.items[5].setHidden("true");

            }
        }

        window.setTimeout(delayIt, 2000);
        //update the undelying table...and it's row total

    },
