        c.field.dataSource = query;
        g.store.proxy.setUrl(finalUrl);
    } else {
        query = queryParts[0];
    }

    //set the dropdown label
    if (ijf.session.filterMode == "custom") {
        var timeFrame = ijf.main.controlSet["ijfContent_fld_4,2"].control.items.items[0];
        timeFrame.suspendEvents();
        timeFrame.setValue("Custom Dates");
        timeFrame.resumeEvents();
    }

    if (!ijf.session.initialized)
        ijf.session.initialized = true;
   // ijf.main.controlSet["ijfContent_fld_2,2"].control.items.items[0].update("Query: " + query.replace("jql=", ""));

    ijf.snippets.JqlFramework.setJQL(query.replace("jql=", ""));

    ijf.snippets["calculateTotalHours"]();

}
