"createWorklogRows = function(inData) {
    ijf.session.worklogReportData = [];

   var isMineOnly=false;
   var myRecordsCtl = ijf.main.controlSet["ijfContent_fld_3,2"].control.items.items[0];
   if(myRecordsCtl.getChecked()[0].boxLabel=="My Logs") isMineOnly=true;

    ijf.snippets.openWorkLog = function(inId) {
        //TODO, this must be based on the customer settings from Atlassian

        var win = window;
        var w = 1200;
        var h = 800;
        var y = win.top.outerHeight / 2 + win.top.screenY - (h / 2)
        var x = win.top.outerWidth / 2 + win.top.screenX - (w / 2)

        window.open(ijf.main.baseSys + "/secure/CreateWorklog!default.jspa?key=" + inId + "&jwt=" + getJwtToken(), "worklogwindow", "menubar=1,resizable=1,width=1200,height=800,top=" + y + ",left=" + x);

    }

    //create return array
    //iterate each issue, populate it's worklog entries 
    //note, the list queries arbitrary fields and repurposes them for the worklog fields

    var retArray = [];

    //At this point you may or may not have worklog from and to.  If you do, you need to
    //filter these rows based on the dates selected
    var toDate = ijf.session.wlToDate;
    var fromDate = ijf.session.wlFromDate;
    if (toDate) {
        //filter out rows
        var tDate = moment(toDate);
        var fDate = moment(fromDate);
    }

    inData.forEach(function(r) {
        //clear values we will write
        var i = 1;

        var aUrl = "/rest/api/2/issue/" + r.iid + "/worklog";
        var rawList = ijfUtils.jiraApiSync('GET', aUrl, null);

        if (rawList.worklogs.length > 0) {
            rawList.worklogs.forEach(function(wl) {

              if((!isMineOnly) || (wl.author.accountId == ijf.main.currentUser.id) || (wl.author.key == ijf.main.currentUser.id))
              {

                //filter out rows if tDate
                var testDate = moment(wl.started).format('YYYY-M-D');
                if (fDate)
                    if (moment(testDate) < fDate) {
                        return;
                        //ok
                    }
                if (tDate)
                    if (moment(testDate) > tDate) {
                        return;
                    }

                var localCmt = wl.comment;
                if (!localCmt)
                    localCmt = "no comment";
                if (localCmt)
                    localCmt = localCmt.replace(/\n/g, "<br>");

                var localCmtShrt = wl.comment;
                if (!localCmtShrt) {
                    localCmtShrt = "_";
                }

                try {
                    var sprint = r.customfield_10000.split("name=")[1].split(",")[0];
                } catch (e) {
                    var sprint = "";
                }

               var urlBase  = "/browse/";
               if(!ijf.snippets.JqlFramework.isServerRuntime())  urlBase =ijf.main.baseSys + urlBase;

                var wlRow = {
                    "key": "<a title='Click to edit Issue' href='" + urlBase + r.key + "' target='_blank'>" + r.key + " (" + i + ")</a>",
                    "project": r.project,
                    "created": r.created,
                    "summary": r.summary,
                    "status": r.status,
                    //"description": "<a href='JAVASCRIPT:ijf.snippets.popFrameworkWlr.onCellClick(\"" + r.key + " (" + i + ")\")'>" + localCmtShrt + "</a>",
                    "description":  localCmtShrt,
                    "duedate": wl.started,
                    "creator": wl.author.displayName,
                    "issuetype": wl.timeSpent,
                    "timeestimate": (wl.timeSpentSeconds / 3600).toFixed(2),
                    "reporter": localCmt,
                    "customfield_10000": sprint,
                    "iid": r.key + " (" + i + ")",
                    "resolution": "na",
                    "wlRaw": wl,
                    "wlStarted": wl.started,
                    "wlTimeSpent": wl.timeSpent,
                    "wlHours": (wl.timeSpentSeconds / 3600).toFixed(2),
                    "wlCreator": wl.author.displayName,
                    "wlCreated": wl.created,
                    "wlComment": wl.comment,
                    "keyOg": r.key,
                };
                i++;
                retArray.push(wlRow);
                ijf.session.worklogReportData[ijf.snippets.popFrameworkWlr.cleanRowId(wlRow.iid)] = wlRow;
             }

            });
        } else {
            //clear log vals for this issue.
            r.description = "";
            r.duedate = "";
            r.creator = "";
            r.issuetype = "";
            r.timeestimate = 0;
            r.iid = "<a href='/browse/" + r.iid + "' target='blank'>" + r.iid + "</a>";
            //retArray.push(r);
        }

    });

    return retArray;
}
"

