setUserFilter=function(f,n,o)
{
    //set an ITEMLIST session variable that will alter the jql of an itemlist
    //syntax:    {"set":[{"status":"Open"}],"remove":["status","project"]]}

  var filterObj = {};
  switch(n["3,2"]) //this is the cell of the radio
  {
       case 1:
             filterObj = {"remove": ["worklogAuthor"]};
       break;

       case 2:
             filterObj = {"set":[{"worklogAuthor ":"currentUser()"}]};
       break;

   }

   //set session var and repaint,  ijfContent_fld_6,2=itemlist
   ijf.session["ijfContent_fld_6,2"]=filterObj;

   //with radio, cannot just reset the form because the group needs to complete...set timeout for +200ms
   var resetForm = function(){
        ijf.main.renderForm("ijfContent", "IFT Worklog Report", false, ijf.currentItem);
        ijfUtils.hideProgress();
   };

   ijfUtils.showProgress();
   window.setTimeout(resetForm,10);

   return true;
} "

