"beforeLoadMyWorklogs=function()
{
   ijf.snippets.beforeLoadSetSystem();
   //ijf.main.baseSys = "https://idealfed.atlassian.net";

  //set the current week for the my worklogs
  //look to reset fromDate and toDates to moments from worklog report overlap

  if(typeof(ijf.session.fromDate)=="string")
  {
       ijf.session.myWorkInitialized=false;
  }

  if(!ijf.session.myWorkInitialized)
  {
      if(!ijf.session.periodMode) ijf.session.periodMode= 'week';

      switch(ijf.session.periodMode)
      {
           case "week":
              ijf.session.periodMode= 'week';
              ijf.session.fromDate  = moment().startOf('isoWeek');
              ijf.session.toDate = moment().endOf('isoWeek')
            break;
           case "month":
              ijf.session.periodMode= 'month';
              ijf.session.fromDate  = moment().startOf('month');
              ijf.session.toDate = moment().endOf('month')
            break;
           case "4 weeks":
              ijf.session.periodMode= '4 weeks';
              ijf.session.fromDate  = moment(new Date()).subtract('day',28);
              ijf.session.toDate = moment(new Date());
            break;

           default:
              ijf.session.periodMode= 'week';
              ijf.session.fromDate  = moment().startOf('isoWeek');
              ijf.session.toDate = moment().endOf('isoWeek')
           break;
      }
   }
  ijf.session.myWorkInitialized=true;

}"

